mapper.select:
	SELECT review_id, user_id, review_title, review_content, review_date
	FROM re_board
 WHERE review_id = #{review_id}

mapper.update:
UPDATE re_board
   SET review_title    =   #{review_title},
   	   review_content  =   #{review_content}
 WHERE review_id = #{review_id}

UPDATE re_board SET
review_views = review_views+1
WHERE review_id = #{review_id}
